<script lang="ts">
import Vue from 'vue';
import {AnimationService} from '@/shared/services/animation.service';
import {SET_SHOW_LOADER} from '@/store/ui';
import {localeMixin} from '@/components/common/mixins/locale.mixin';
export default Vue.extend({
  name: 'Loader',
  mixins: [localeMixin],
  mounted(): void {
    AnimationService.timeLine({
      duration: 1.5
    }).from('.letter', {
      opacity: 0,
      x: -10,
      stagger: -0.1,
      ease: AnimationService.easing.power1.easeInOut
    }).from('.letter2', {
      duration: 0.5,
      delay: -1.5,
      rotate: 5,
      opacity: 0,
      y: 3,
      stagger: 0.1,
      ease: AnimationService.easing.power1.easeInOut,
      onComplete: () => this.animate()
    }).to('.letter, .letter2', {
      delay: 0.6,
      fill: '#FF0092'
    }).to('.letter', {
      duration: 1,
      delay: 0.6,
      fill: '#0455BF',
      stagger: 0.05,
      scale: 0.95,
      ease: AnimationService.easing.power1.easeInOut
    })
      .to('.loader', {
        delay: -0.8,
        opacity: 0,
        zIndex: -2
      }).to('.loader', {
        display: 'none',
        onComplete: () => this.loaderFinished()
      })
  },
  methods: {
    animate() {
      const timeline = AnimationService.anime.timeline({
        easing: 'easeInOutQuint',
        duration: 1300,
        loop: false
      });

      timeline
        .add({
          targets: '.morph',
          d: [
            {
              value: 'M2665.84 446.529C2665.84 779.119 94.54 2860.3 -286.03 2860.3C-484.267 2860.3 104.03 1362.46 -22.0728 1195.97C-469.478 605.294 -2511.03 409.373 -2537.51 263.694C-2537.51 -68.8955 1152.41 -173.988 1532.98 -173.988C1913.55 -173.988 2665.84 113.94 2665.84 446.529Z'
            }],
          easing: 'easeInOutCubic'
        })
        .add({
          targets: '.morph',
          d: [
            {
              value: 'M443.516 1153.58C443.516 1486.17 888.113 1280.92 507.543 1280.92C309.306 1280.92 443.594 1243.7 443.595 1032.83C443.595 291.839 466.255 122.978 439.768 -22.7012C439.768 -355.291 -267.857 -179.827 112.712 -179.827C493.282 -179.827 443.516 -585.23 443.516 1153.58Z'
            }],
          easing: 'easeInCubic'
        });

      timeline.play();
    },
    loaderFinished() {
      this.$store.commit(SET_SHOW_LOADER, true);
    }
  }
});
